using System;using System.Collections.Generic;using System.Diagnostics;using System.Linq;using System.Threading.Tasks;using Microsoft.AspNetCore.Mvc;using JobStation.Models;using Microsoft.Extensions.Logging;using Microsoft.AspNetCore.Authorization;namespace JobStation.Controllers{    [Authorize(Roles = "Manager,Leader,Employee")]//只允许3个角色访问HomeController    public class HomeController : Controller    {        private readonly ILogger<HomeController> _logger;        public HomeController(ILogger<HomeController> logger)        {            _logger = logger;        }        public IActionResult Index()        {            _logger.LogInformation("Index page says hello");            return View();        }        public IActionResult About()        {            ViewData["Message"] = "Your application description page.";            return View();        }        public IActionResult Contact()        {            ViewData["Message"] = "Your contact page.";            return View();        }        public IActionResult Error()        {            return View();        }        [AllowAnonymous]//所有人都可以访问        [HttpGet("login")]        public IActionResult Login()        {            return View();        }        [AllowAnonymous]//所有人都可以访问        [HttpPost("login")]        public IActionResult Login(string username, string password)        {            return View();        }    }}